import React from 'react';
import '../styles/styles.css';
import { jobs_ar } from "../data/jobs";


const JobCard = ({ title, date, time, description, imageUrl }) => {
  return (
    <div className="container">
            {jobs_ar.map((item, index) => (
            <div key={index} className="job-item">
                <div className="text-content">
                <div className="img-container">
                    <img src={item.mainImg} alt="" className="main-img" />
                </div>
                <div className="text-wrapper">
                    <div className="text-content">
                    <img src={item.countryFlag} alt="" width={19} />
                    <a href="" className="button-link">â†’</a>
                    <p>{item.country}, {item.city}</p>
                    <p>{item.jobType}</p>
                    <p>{item.salary}</p>
                    </div>
                </div>
                </div>
            </div>
            ))}
        </div>
  );
};

const JobList = () => {
  const jobs = [
    {
      title: 'Software Engineer',
      date: '2023-05-20',
      time: '', // Remove time if not applicable
      description: 'Lorem ipsum dolor sit amet...',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image
    },
    {
      title: 'Data Analyst',
      date: '2023-05-20',
      time: '', // Remove time if not applicable
      description: 'Lorem ipsum dolor sit amet...',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image
    },
    {
      title: 'Marketing Manager',
      date: '2023-05-20',
      time: '', // Remove time if not applicable
      description: 'Lorem ipsum dolor sit amet...',
      imageUrl: 'https://via.placeholder.com/150', // Placeholder image
    },
    // Add more job objects here
  ];

  return (
    <section className="demo">
      <div className="container">
        <div id="owl-example" className="owl-carousel">
          {jobs_ar.map((job) => (
            <JobCard key={job.title} {...job} />
          ))}
        </div>
      </div>
    </section>
  );
};
export default JobList;
