import React from 'react';
import '../styles/styles.css';

const EventCard = ({ title, date, time, description, imageUrl, registrationLink }) => {
  return (
    <div className="single_courses">
      <div className="single_courses_thumb">
        <img src={imageUrl} alt="Event" width="100%" />
      </div>
      <div className="single_courses_desc">
        <div className="title">
          <a href="#">
            <h5>{title}</h5>
          </a>
          <div className="date_time">
            <div className="date">
              <p>
                <span className="icon-calendar"></span> {date}
              </p>
            </div>
            <div className="time">
              <p>
                <span className="icon-clock"></span> {time}
              </p>
            </div>
          </div>
          <p>{description}</p>
        </div>
        {registrationLink && (
          <div className="registration-button">
            <a href={registrationLink}>
              <button>הרשמה</button>
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

const EventList = () => {
  const events = [
    {
      title: '7 דרכים קלות להפוך אירוע מוצלח',
      date: '16 בספטמבר, 2017',
      time: '20:30',
      description: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit. Hic sunt',
      imageUrl: 'https://s10.postimg.org/5h6on1huh/event_1.jpg',
      registrationLink: 'https://example.com/register/event1',
    },
    // Add more event objects here
  ];

  return (
    <section className="demo">
      <div className="container">
        <div id="owl-example" className="owl-carousel">
          {events.map((event) => (
            <EventCard
              key={event.title}
              {...event}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default EventList;
